<template>
  <div>
    <v-data-table
        :headers="headers"
        :items="desserts"
        :sort-by.sync="sortBy"
        :sort-desc.sync="sortDesc"
        :loading="load"
        multi-sort
        :search="search"
        loading-text="Waiting"
    >
      <template v-slot:no-data>暂无维修记录</template>
      <template v-slot:no-results>无匹配记录</template>
      <!--  颜色  -->
      <template v-slot:item.fix_status="{item }">
        <v-chip :color="getColor(item.fix_status)" dark outlined>{{ item.fix_status}}</v-chip>
      </template>

      <template v-slot:top>
        <v-toolbar flat>
          <v-toolbar-title>维修详情</v-toolbar-title>
          <v-divider class="mx-4" inset vertical></v-divider>
          <v-text-field
              v-model="search"
              append-icon="mdi-magnify"
              label="Search"
              single-line
              hide-details
          ></v-text-field>
          <v-spacer/>
          <v-dialog v-model="dialog" max-width="520px">
            <v-card>
              <v-img
                  class="white--text align-end"
                  height="200px"
                  :src="iconBase64"
              >
              <v-card-title>
                <span class="headline">编辑维修项目</span>
              </v-card-title>
              </v-img>

              <v-card-text>
                <v-container>
                    <v-col>
                      <v-file-input
                          counter
                          chips
                          truncate-length="15"
                          accept="image/*"
                          @change="see"
                          v-model="fileInfo"
                          :error-messages="fileErrors"
                          @input="$v.fileInfo.$touch()"
                          @blur="$v.fileInfo.$touch()"
                      ></v-file-input>
                    </v-col>

                    <v-col cols="10" sm="10" md="10">
                      <v-textarea
                          outlined
                          v-model="fix_log"
                          label="维修过程记录"
                          rows="3"
                          counter="200"
                          required
                          :error-messages="logErrors"
                          @input="$v.fix_log.$touch()"
                          @blur="$v.fix_log.$touch()"
                      ></v-textarea>
                    </v-col>
                  <small>带*为必填项</small>
                </v-container>
              </v-card-text>

              <v-card-actions>
                <v-spacer/>
                <v-btn color="blue darken-1" text @click="close">
                  取 消
                </v-btn>
                <v-btn color="blue darken-1" text @click="save">
                  上 传
                </v-btn>
              </v-card-actions>
            </v-card>
          </v-dialog>

          <v-dialog class="rounded-0" v-model="dialogfixlog" max-width="520px">
            <v-card  class="rounded-0">
                <v-card-title>
                  <span class="headline">查看维修过程记录</span>
                </v-card-title>

              <v-card-text>
                <v-timeline
                    align-top
                    dense
                >
                  <v-timeline-item
                      v-for="(item, i) in tl"
                      :key="i"
                      small
                  >
                    <div>
                      <div class="font-weight-normal">
                        <v-row>
                        <strong>{{ item.title }}</strong>
                        </v-row>
                        @{{ item.time }}
                      </div>
                      <div>{{ item.log }}</div>
                      <v-img
                      :src="item.pic"
                      contain
                      max-height="200"
                      >
                      </v-img>
                    </div>
                  </v-timeline-item>
                </v-timeline>
              </v-card-text>

              <v-card-actions>
                <v-spacer/>
                <v-btn color="blue darken-1" text @click="closefixlog">
                  关 闭
                </v-btn>
              </v-card-actions>
            </v-card>
          </v-dialog>


        </v-toolbar>
      </template>
      <!-- 这里是action里面的图标   -->
      <template v-slot:item.actions="{ item }">
          <v-btn icon color="primary" class="elevation-5 ma-2" @click="editItem(item)">
            <v-icon small  >mdi-pencil</v-icon>
          </v-btn>
        <v-btn  v-if="item.fix_status!=='未处理'" icon color="primary" class="elevation-5 ma-2" @click="timelineItem(item)">
          <v-icon small  >{{mdiTimelineTextOutline}}</v-icon>
        </v-btn>
        <v-btn v-if="item.fix_status!=='已处理'" icon color="primary" class="elevation-5 ma-2" @click="confirmfixmd(item)">
          <v-icon small  >{{ mdiCheck  }}</v-icon>
        </v-btn>
      </template>

    </v-data-table>
    <v-snackbar
        top
        v-model="bar"
        :timeout="3000"
    >
      {{ mess }}
      <template v-slot:action="{ attrs }">
        <v-btn
            color="blue"
            text
            v-bind="attrs"
            @click="bar = false"
        >
          关 闭
        </v-btn>
      </template>
    </v-snackbar>
    <v-snackbar
        top
        v-model="confirmfix"
    >
      确定已完成维修吗？
      <template v-slot:action="{ attrs }">
        <v-btn
            color="blue"
            text
            v-bind="attrs"
            @click="final"
        >
          确 定
        </v-btn>
      </template>
    </v-snackbar>
  </div>
</template>

<script>
import { validationMixin } from 'vuelidate'
import { required,maxLength} from 'vuelidate/lib/validators'
import { mdiHandPointingRight,mdiTimelineTextOutline,mdiCheck  } from '@mdi/js';
export default {
  mixins: [validationMixin],

  validations: {
    fix_log:{required,maxLength:maxLength(200)},
    fileInfo:{required},
  },
  data: () => ({
    confirmfix:false,
    mdiCheck:mdiCheck,
    tl:[],
    fix_log:null,
    iconBase64:null,
    fileInfo:null,
    dialogfixlog:false,
    base:"data:image/jpeg;base64,",
    mdiTimelineTextOutline:mdiTimelineTextOutline,
    mdiHandPointingRight:mdiHandPointingRight,
    url: process.env.VUE_APP_API,
    load:true,
    mess:"",bar:false,
    modal: false,
    search:"",
    sortBy:"fix_startime",
    sortDesc:false,
    dialog: false,
    dialogDelete: false,
    headers: [
      { text: '业主姓名', align: 'start', value: 'cust_name',},
      { text: '地址', value: 'cust_addr' },
      { text: '维修状态', value: 'fix_status' },
      { text: '生成时间', value: 'fix_startime' },
      { text: '完成时间', value: 'fix_endtime' },
      { text: '维修详情', value: 'fix_log' },
      { text: '处理人', value: 'admin_name' },
      { text: 'Actions', value: 'actions', sortable: false },
    ],
    desserts: [],
    editedIndex: -1,
    editedItem: {admin_login:'', cust_name: '', fix_endtime:'', fix_id:'',
      fix_log:'', fix_startime:'', fix_status:'', admin_id:'', admin_name:'', status:false,
    },
    defaultItem: {admin_login:'', cust_name: '', fix_endtime:'', fix_id:'', fix_log:'',
      fix_startime:'', fix_status:'', admin_id:'', admin_name:'', status:false,
    },
    confirmfixx:[],
  }),

  computed: {
    logErrors () {
      const errors = []
      if (!this.$v.fix_log.$dirty) return errors
      !this.$v.fix_log.maxLength && errors.push('详情要少于50个字符')
      !this.$v.fix_log.required && errors.push('详情不可为空')
      return errors
    },
    fileErrors() {
      const errors = []
      if (!this.$v.fileInfo.$dirty) return errors
      !this.$v.fileInfo.required && errors.push('文件不可为空')
      return errors
    },
  },

  watch: {
    dialog (val) {val || this.close()},
    dialogDelete (val) {val || this.closeDelete()},
  },

  created () {this.initialize()},

  methods: {
    confirmfixmd(item){
      this.confirmfix = true
      this.confirmfixx =item
    },
    final(){
      this.confirmfix = false
      var mess = {'id':this.confirmfixx.fix_id}
      this.axios.post(this.url+'userFix/finalfix',JSON.stringify(mess))
      .then(res=>{
        this.mess = res.data
        this.bar = true
            this.initialize()
      })
    },
    closefixlog(){
      this.dialogfixlog = false
      this.tl=null
    },
    timelineItem(item){
      this.dialogfixlog = true
      var mess = {'id':item.fix_id}
      this.axios.post(this.url+'userFix/queryFixlog',JSON.stringify(mess))
          .then(res => {
            this.tl=res.data;
            this.loadin=!this.loadin;
            this.dialogfixlog = true
          },res => {
            console.log(res);
          })
    },
    allowedDates: val => Date.parse(val) > Date.now() - 8.64e7,
    getColor (calories) {
      if (calories === "已处理") return 'primary'
      else return 'red'
    },
    see(file) {
      if (!(this.$v.fileInfo.$invalid||this.$v.fileInfo.$error)){
        this.viewquary = null
        var reader = new FileReader()
        reader.readAsDataURL(file)
        reader.onload = () => {this.iconBase64 = reader.result}
        reader.onerror = function (error) {
          console.log('Error: ', error)
        }
      }
    },
    initialize () {
      this.iconBase64 = this.base
      this.load = true
      this.editedItem = Object.assign({}, this.defaultItem)
      this.editedIndex = -1
      this.axios.post(this.url+'userFix/queryUserFixbyfixer',JSON.stringify({'name':window.localStorage.getItem('name')}))
          .then(res => {
            this.desserts=res.data;
            this.loadin=!this.loadin;
          },res => {
            console.log(res);
          })
      this.load = false
    },

    switchfix(){
      if(!this.editedItem.status){
        this.editedItem.admin_name = ''
        this.editedItem.fix_endtime = ''
      }else{
        this.editedItem.admin_name = window.sessionStorage.getItem('name')
        this.editedItem.fix_endtime = new Date().toJSON().substring(0, 10) + ' ' + new Date().toTimeString().substring(0,8)
      }
    },

    editItem (item) {
      this.editedIndex = this.desserts.indexOf(item)
      this.editedItem = Object.assign({}, item)
      this.dialog = true
    },

    deleteItem (item) {
      this.editedIndex = this.desserts.indexOf(item)
      this.editedItem = Object.assign({}, item)
      this.dialogDelete = true
    },

    deleteItemConfirm () {
      this.desserts.splice(this.editedIndex, 1)
      var delfix = {'id':this.editedItem.fix_id}
      this.axios.post(this.url+'userFix/DelFix', JSON.stringify(delfix))
      this.mess = "删除成功"
      this.bar = true
      this.closeDelete()
    },

    close () {
      this.dialog = false
      this.$nextTick(() => {
        this.editedItem = Object.assign({}, this.defaultItem)
        this.editedIndex = -1
      })
      this.$v.$reset()
      this.iconBase64 = this.base
      this.fileInfo = null
      this.fix_log = null
    },

    closeDelete () {
      this.dialogDelete = false
      this.$nextTick(() => {
        this.editedItem = Object.assign({}, this.defaultItem)
        this.editedIndex = -1
      })
    },
    save () {
      if (this.$v.fileInfo.$invalid||this.$v.fileInfo.$error||this.$v.fix_log.$invalid||this.$v.fix_log.$error){
        this.$v.$touch()
      }
      else {
        var mess = this.editedItem
        mess = {'id':mess.fix_id,'log':this.fix_log,'pic':this.iconBase64,'name':mess.fixer_name}
        this.axios.post(this.url+'userFix/addtimeline', JSON.stringify(mess))
        this.mess = "上传成功"
        this.bar = true
        this.initialize()
        this.close()
      }
    },
  },
}
</script>